import React, { useState, useEffect } from "react";
function App() {
  const [initFocus, setInitFocus] = useState(25*60); //valores dos quadros
  const [initInterval, setInitInterval] = useState(5*60); //valores dos quadros
  const [timeFocus, setTimeFocus] = useState(25*60); //valores que ser達o modificados no cronometro
  const [timeInterval, setTimeInterval] = useState(5*60); //valores que ser達o modificados no cronometro

  function reduceTimeFocus(){
    if(timeFocus > 0){
      setTimeFocus(timeFocus-60);
    }else{
      alert("O valor precisa ser maior que zero.")
    }
  }

  function reduceTimeInterval(){
    if(timeInterval > 0){
      setTimeInterval(timeInterval-60);
    }else{
      alert("O valor precisa ser maior que zero.")
    }
  }

  function addTimeFocus(){
    setTimeFocus(timeFocus+60);
  }

  function reduceTimeInterval(){
    setTimeInterval(timeInterval+60);
  }

  function convertMinutes(seconds){
    return Math.floor(seconds/60);
  }

  return(
    <div>
      Tempo de foco <br></br>
      <h1>{convertMinutes(timeFocus)}</h1>
    </div>
  );
}

export default App;
