import React, { useState, useEffect } from "react";
function App() {
  const [timeFocus, setTimeFocus] = useState(25*60);
  const [timeInterval, setTimeInterval] = useState(5*60);

  function reduceTimeFocus(){
    if(timeFocus > 0){
      setTimeFocus(timeFocus-60);
    }else{
      alert("O valor precisa ser maior que zero.")
    }
  }

  function reduceTimeInterval(){
    if(timeInterval > 0){
      setTimeInterval(timeInterval-60);
    }else{
      alert("O valor precisa ser maior que zero.")
    }
  }

  function addTimeFocus(){
    setTimeFocus(timeFocus+60);
  }

  function reduceTimeInterval(){
    setTimeInterval(timeInterval+60);
  }

  function convertMinutes(seconds){
    return Math.floor(seconds/60);
  }

  const calcMinutesFocus = Math.floor(secondsFocus/60);
  const calcSecondsFocus = secondsFocus%60;
  const timerMinutesFocus = calcMinutesFocus < 10 ? `0${calcMinutesFocus}` : calcMinutesFocus;
  const timerSecondsFocus = calcSecondsFocus < 10 ? `0${calcSecondsFocus}` : calcSecondsFocus;

  return(
    <div>
      Tempo de foco <br></br>
    </div>
  );
}

export default App;
