import React, { useState, useEffect } from "react";
function App() {
  const [timeFocus, setTimeFocus] = useState(25*60);
  const [timeInterval, setTimeInterval] = useState(5*60);

  function reduceTimeFocus(){
    if(timeFocus > 0){
      setTimeFocus(timeFocus-1);
    }else{
      alert("O valor precisa ser maior que zero")
    }
  }

  function reduceTimeInterval(){
    setTimeInterval(timeInterval-1);
  }
}

export default App;
