import React, { useState, useEffect } from "react";
import "./App.css"
import tomate from './assets/tomate.png';

const FOCUS = 0;
const INTERVAL = 1;

function App() {
  const [initFocus, setInitFocus] = useState(25); //valores dos quadros
  const [initInterval, setInitInterval] = useState(5); //valores dos quadros
  const [timer, setTimer] = useState(25*60); //valores que serão modificados no cronometro
  const [status, setStatus] = useState(false);
  const [type, setType] = useState(FOCUS);
  //funcao para reduzir tempo de foco pelo botao
  function reduceTimeFocus(){
    if(initFocus <= 1){
      alert("O valor precisa ser maior que zero.")
    }
    else{
      setInitFocus(initFocus-1);
    }
  }

  //funcao para reduzir tempo de intervalo pelo botao
  function reduceTimeInterval(){
    if(initInterval <= 1){
      alert("O valor precisa ser maior que zero.")
    }else{
      setInitInterval(initInterval-1);
    }
  }

  //funcao para adicionar tempo de foco pelo botao
  function addTimeFocus(){
    setInitFocus(initFocus+1);
  }

  //funcao para adicionar tempo de intervalo pelo botao
  function addTimeInterval(){
    setInitInterval(initInterval+1);
  }

  /*Operações sobre o cronometro*/

  function startTimer(){ //ativa o cronometro
    setStatus(true);
  }

  function pauseTimer(){ //pausa o cronometro
    setStatus(false);
  }

  function resetTimer(){ //reinicia o cronometro
    setStatus(false);
    if(type == FOCUS){
      setTimer(initFocus*60);
    }else{
      setTimer(initInterval*60);
    }
  }

  //funcoes para auxiliar na contagem do cronometro

  function decrementSeconds(){ //decrementa os segundos de tempo de foco
    if(timer === 0){
      if(type == FOCUS){
        setType(INTERVAL);
        setTimer(initInterval*60);
      }else{
        setType(FOCUS);
        setTimer(initFocus*60);
      }
    }e
  }

  //realiza as operações sobre o cronometro acompanhando os estados
  useEffect(() => {
    let interval = setInterval(() => {
      clearInterval(interval);

      if(statusFocus === false && timeFocus === 0){
        setStatusInterval(true);
        decrementSecondsInterval();
      }else if(statusInterval === false && timeInterval === 0){
        setStatusFocus(true);
        decrementSecondsFocus();
      }else if(statusFocus === true && timeFocus != 0){
        console.log("statusFocus === true");
        decrementSecondsFocus();
      }else if(statusInterval === true && timeInterval !== 0){
        decrementSecondsInterval();
      }
    }, 1000);
  }, [statusFocus, statusInterval, timeFocus, timeInterval]);


  //preparando os segundos para mostrar na tela

  //converte segundos em minutos
  function getMinutes(seconds){
    return Math.floor(seconds/60);
  }

  //pega a parte dos segundos
  function getSeconds(seconds){
    return seconds%60;
  }

  const minutesFocus = getMinutes(timeFocus);
  const secondsFocus = getSeconds(timeFocus);
  const screenMinutesFocus = minutesFocus < 10 ? `0${minutesFocus}` : minutesFocus;
  const screenSecondsFocus = secondsFocus < 10 ? `0${secondsFocus}` : secondsFocus;

  const minutesInterval = getMinutes(timeInterval);
  const secondsInterval = getSeconds(timeInterval);
  const screenMinutesInterval = minutesInterval < 10 ? `0${minutesInterval}` : minutesInterval;
  const screenSecondsInterval = secondsInterval < 10 ? `0${secondsInterval}` : secondsInterval;

  return(
    <div>
      <div className="container">
        <div className="boxConfig">
          <a className="title">Tempo de foco</a>
          <h1 className="initTime">{initFocus}</h1>
          <button className="btn-red-qa" onClick={reduceTimeFocus}>-</button>
          <button className="btn-green-qa" onClick={addTimeFocus}>+</button>
        </div>

        <div className="boxConfig">
          <a className="title">Tempo de intervalo</a>
          <h1 className="initTime">{initInterval}</h1>
          <button className="btn-red-qa" onClick={reduceTimeInterval}>-</button>
          <button className="btn-green-qa" onClick={addTimeInterval}>+</button>
        </div>
      </div>

      <div className="cronometro">
        <img src={tomate} className="img"/>
        <div className="menu">
            <h1 className="menuTitle">Foco!</h1>
            {screenMinutesFocus}:{screenSecondsFocus}
            <button onClick={startTimeFocus}>Iniciar</button>
            <button onClick={pauseTimeFocus}>Pausar</button>
            <button onClick={resetTimeFocus}>Resetar</button> 
        </div>     
      </div>

      <div className="cronometro">
        <img src={tomate} className="img"/>
        <div className="menu">
            <h1 className="menuTitle">Intervalo:</h1>
            {screenMinutesInterval}:{screenSecondsInterval}
            <button onClick={startTimeInterval}>Iniciar</button>
            <button onClick={pauseTimeInterval}>Pausar</button>
            <button onClick={resetTimeInterval}>Resetar</button> 
        </div>     
      </div>
  </div>
  );
}

export default App;
