import React, { useState, useEffect } from "react";
function App() {
  const [initFocus, setInitFocus] = useState(25); //valores dos quadros
  const [initInterval, setInitInterval] = useState(5); //valores dos quadros
  const [timeFocus, setTimeFocus] = useState(25*60); //valores que serão modificados no cronometro
  const [timeInterval, setTimeInterval] = useState(5*60); //valores que serão modificados no cronometro
  const [statusFocus, setStatusFocus] = useState(false);
  const [statusInterval, setStatusInterval] = useState(false);

  //funcao para reduzir tempo de foco pelo botao
  function reduceTimeFocus(){
    if(initFocus > 0){
      setInitFocus(initFocus-1);
      setTimeFocus(timeFocus-60);
    }else{
      alert("O valor precisa ser maior que zero.")
    }
  }

  //funcao para reduzir tempo de intervalo pelo botao
  function reduceTimeInterval(){
    if(initInterval > 0){
      setInitInterval(initInterval-1);
      setTimeInterval(timeInterval-60);
    }else{
      alert("O valor precisa ser maior que zero.")
    }
  }

  //funcao para adicionar tempo de foco pelo botao
  function addTimeFocus(){
    setInitFocus(initFocus+1);
    setTimeFocus(timeFocus+60);
  }

  //funcao para adicionar tempo de intervalo pelo botao
  function addTimeInterval(){
    setInitInterval(initInterval+1);
    setTimeInterval(timeInterval+60);
  }

  /*Operações sobre o cronometro*/

  function startTimeFocus(){ //ativa o cronometro
    setStatusFocus(true);
  }

  function pauseTimeFocus(){ //pausa o cronometro
    setStatusFocus(false);
  }

  function resetTimeFocus(){ //reinicia o cronometro de foco
    setStatusFocus(false);
    setTimeFocus(initFocus);
  }

  function startTimeInterval(){ //ativa o cronometro
    setStatusInterval(true);
  }

  function pauseTimeInterval(){ //pausa o cronometro
    setStatusInterval(false);
  }

  function resetTimeInterval(){ //reinicia o cronometro de intervalo
    setStatusInterval(false);
    setTimeInterval(initInterval);
  }

  /*as seções vão acontecer da seguinte maneira:
    o cronometro de intervalo começa quando status for false e timeFocus for igual a zero.
    cronometro de foco reativa automaticamente quando status for false e timeInterval for igual a zero.*/

  //funcoes para auxiliar na contagem do cronometro

  function decrementSecondsFocus(){ //decrementa os segundos de tempo de foco
    setTimeFocus(timeFocus-1);
    if(timeFocus === 0){
      setStatusFocus(false);
    }
  }

  function decrementSecondsInterval(){ //decrementa os segundos de tempo de intervalo
    setTimeInterval(timeInterval-1);
    if(timeInterval === 0){
      setStatusInterval(false);
    }
  }

  //realiza as operações sobre o cronometro acompanhando os estados
  useEffect(() => {
    let interval = setInterval(() => {
      clearInterval(interval);

      if(statusFocus === false && timeFocus === 0){
        setStatusInterval(true);
        decrementSecondsInterval();
      }else if(statusInterval === false && timeInterval === 0){
        setStatusFocus(true);
        decrementSecondsFocus();
      }else if(statusFocus === true && timeFocus != 0){
        console.log("statusFocus === true");
        decrementSecondsFocus();
      }else if(statusInterval === true && timeInterval !== 0){
        decrementSecondsInterval();
      }
    }, 1000);
  }, [statusFocus, statusInterval, timeFocus, timeInterval]);


  //preparando os segundos para mostrar na tela

  //converte segundos em minutos
  function getMinutes(seconds){
    return Math.floor(seconds/60);
  }

  //pega a parte dos segundos
  function getSeconds(seconds){
    return seconds%60;
  }

  const minutesFocus = getMinutes(timeFocus);
  const secondsFocus = getSeconds(timeFocus);
  const screenMinutesFocus = minutesFocus < 10 ? `0${minutesFocus}` : minutesFocus;
  const screenSecondsFocus = secondsFocus < 10 ? `0${secondsFocus}` : secondsFocus;

  const minutesInterval = getMinutes(timeInterval);
  const secondsInterval = getSeconds(timeInterval);
  const screenMinutesInterval = minutesInterval < 10 ? `0${minutesInterval}` : minutesInterval;
  const screenSecondsInterval = secondsInterval < 10 ? `0${secondsInterval}` : secondsInterval;

  return(
    <div className="container">
      Tempo de foco <br></br>
      <h1>{initFocus}</h1>
      <button onClick={reduceTimeFocus}>-</button>
      <button onClick={addTimeFocus}>+</button>
    <br></br><br></br>
      Tempo de intervalo<br></br>
      <h1>{initInterval}</h1>
      <button onClick={reduceTimeInterval}>-</button>
      <button onClick={addTimeInterval}>+</button>

      <br></br><br></br>
      <div className="message">
         <div>
           <h1>Tempo de foco</h1>
           {screenMinutesFocus}:{screenSecondsFocus}</div>
      </div>
    </div>


  );
}

export default App;
